<div>#!/bin/bash<br/># Inshackle v1.0<br/># recoded by: AK TEC HUNT3r<br/># github.com/aktechunt3r/inshackle<br/># If you use any part from this code, give me the credits, please, read the License<div></div><br/>string4=$(openssl rand -hex 32 | cut -c 1-4)<br/>string8=$(openssl rand -hex 32  | cut -c 1-8)<br/>string12=$(openssl rand -hex 32 | cut -c 1-12)<br/>string16=$(openssl rand -hex 32 | cut -c 1-16)<br/>device="android-$string16"<br/>uuid=$(openssl rand -hex 32 | cut -c 1-32)<br/>phone="$string8-$string4-$string4-$string4-$string12"<br/>guid="$string8-$string4-$string4-$string4-$string12"<br/>header='Connection: "close", "Accept": "*/*", "Content-type": "application/x-www-form-urlencoded; charset=UTF-8", "Cookie2": "$Version=1" "Accept-Language": "en-US", "User-Agent": "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"'<br/>var=$(curl -i -s -H "$header" https://i.instagram.com/api/v1/si/fetch_headers/?challenge_type=signup&guid=$uuid > /dev/null)<br/>var2=$(echo $var | grep -o 'csrftoken=.*' | cut -d ';' -f1 | cut -d '=' -f2)<br/>ig_sig="4f8732eb9ba7d1c8e8897a75d6474d4eb3f5279137431b2aafb71fafe2abe178"<div></div><div></div>banner() {<br/>printf " \n"<br/>printf " \e[1;31mM\"\"M\e[0m\e[1;77m                   \e[0m\e[1;93mdP                         dP       \e[0m\e[1;77mdP           \e[0m\n"<br/>printf " \e[1;31mM  M\e[0m\e[1;77m                   \e[0m\e[1;93m88                         88       \e[0m\e[1;77m88           \e[0m\n"<br/>printf " \e[1;31mM  M\e[0m\e[1;77m 88d888b. .d8888b. \e[0m\e[1;93m88d888b. .d8888b. .d8888b. 88  .dP  \e[0m\e[1;77m88 .d8888b.  \e[0m\n"<br/>printf " \e[1;31mM  M\e[0m\e[1;77m 88'  \`88 Y8ooooo. \e[0m\e[1;93m88\'  \`88 88\'  \`88 88'  \`\"\" 88888\"   \e[0m\e[1;77m88 88ooood8  \e[0m\e[0m\n"<br/>printf " \e[1;31mM  M\e[0m\e[1;77m 88    88       88 \e[0m\e[1;93m88    88 88.  .88 88.  ... 88  \`8b. \e[0m\e[1;77m88 88.  ...  \e[0m\n"<br/>printf " \e[1;31mM  M\e[0m\e[1;77m dP    dP \`88888P\' \e[0m\e[1;93mdP    dP \`88888P8 \`88888P\' dP   \`YP \e[0m\e[1;77mdP \`88888P\'  \e[0m\n"<br/>printf " \e[1;31mMMMM\e[0m                                                                    \n"<br/>printf "\n"<div></div>printf "      \e[1;31m[\e[0m\e[1;77mv1.0\e[0m\e[1;31m]\e[0m\e[1;77m recoded by AK TEC HUNT3r (thanks to linuxchoice)\e[0m\n"<br/>}<div></div><div></div>login_user() {<div></div><br/>if [[ $user == "" ]]; then<br/>printf "\e[1;31m[\e[0m\e[1;77m*\e[0m\e[1;31m]\e[0m\e[1;93m Login\e[0m\n"<br/>read -p $'\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Username: \e[0m' user<br/>fi<div></div>if [[ -e cookie.$user ]]; then<div></div>printf "\e[1;31m[\e[0m\e[1;77m*\e[0m\e[1;31m]\e[0m\e[1;93m Cookies found for user\e[0m\e[1;77m %s\e[0m\n" $user<div></div>default_use_cookie="Y"<div></div>read -p $'\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Use it?\e[0m\e[1;77m [Y/n]\e[0m ' use_cookie<div></div>use_cookie="${use_cookie:-${default_use_cookie}}"<div></div>if [[ $use_cookie == *'Y'* || $use_cookie == *'y'* ]]; then<br/>printf "\e[1;31m[\e[0m\e[1;77m*\e[0m\e[1;31m]\e[0m\e[1;93m Using saved credentials\e[0m\n"<br/>else<br/>rm -rf cookie.$user<br/>login_user<br/>fi<div></div><br/>else<div></div>read -s -p $'\e[1;31m[\e[0m\e[1;77m*\e[0m\e[1;31m]\e[0m\e[1;93m Password: \e[0m' pass<br/>printf "\n"<br/>data='{"phone_id":"'$phone'", "_csrftoken":"'$var2'", "username":"'$user'", "guid":"'$guid'", "device_id":"'$device'", "password":"'$pass'", "login_attempt_count":"0"}'<div></div>IFS=$'\n'<div></div>hmac=$(echo -n "$data" | openssl dgst -sha256 -hmac "${ig_sig}" | cut -d " " -f2)<br/>useragent='User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"'<div></div>printf "\e[1;77m[\e[0m\e[1;92m+\e[0m\e[1;77m] Trying to login as\e[0m\e[1;93m %s\e[0m\n" $user<br/>IFS=$'\n'<br/>var=$(curl -c cookie.$user -d "ig_sig_key_version=4&signed_body=$hmac.$data" -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"' -w "\n%{http_code}\n" -H "$header" "https://i.instagram.com/api/v1/accounts/login/" | grep -o "logged_in_user\|challenge\|many tries\|Please wait" | uniq ); <br/>if [[ $var == "challenge" ]]; then printf "\e[1;93m\n[!] Challenge required\n" ; exit 1; elif [[ $var == "logged_in_user" ]]; then printf "\e[1;92m \n[+] Login Successful\n" ; elif [[ $var == "Please wait" ]]; then echo "Please wait"; fi; <div></div>fi<div></div>}<div></div><br/>get_saved() {<br/>user_account=$user<br/>user_id=$(curl -L -s 'https://www.instagram.com/'$user_account'' > getid && grep -o  'profilePage_[0-9]*.' getid | cut -d "_" -f2 | tr -d '"')<div></div>printf "\e[1;77m[\e[0m\e[1;92m+\e[0m\e[1;77m] Generating image list\n"<br/>curl -L -b cookie.$user -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"' -w "\n%{http_code}\n" -H "$header" "https://i.instagram.com/api/v1/feed/saved" > $user_account.saved_ig<div></div>cp $user_account.saved_ig $user_account.saved_ig.00<br/>count=0<div></div>while [[ true ]]; do<br/>big_list=$(grep -o '"more_available": true' $user_account.saved_ig)<br/>maxid=$(grep -o '"next_max_id": "[^ ]*.' $user_account.saved_ig | cut -d " " -f2 | tr -d '"' | tr -d ',')<div></div>if [[ $big_list == *'"more_available": true'* ]]; then<div></div>url="https://i.instagram.com/api/v1/feed/saved/?rank_token=$user_id\_$guid&max_id=$maxid"<div></div>curl -L -b cookie.$user -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"'  -H "$header" "$url" > $user_account.saved_ig<div></div>cp $user_account.saved_ig $user_account.saved_ig.$count<div></div>unset maxid<br/>unset url<br/>unset big_list<br/>else<br/>grep -o '{"width": [0-9]*, "height": [0-9]*, "url": "https://[^ ]*' $user_account.saved_ig* | cut -d " " -f6 | cut -d '"' -f2  | cut -d "\\" -f1 | uniq > links<br/>break<div></div>fi<div></div>let count+=1<div></div>done<div></div><br/>if [[ ! -d $user/images ]]; then<br/>mkdir -p $user/images<br/>fi<br/>tot_img=$(wc -l links | cut -d " " -f1)<br/>count_img=0<br/>printf "\e[1;77m[\e[0m\e[1;31m+\e[0m\e[1;77m] Total images:\e[0m\e[1;93m %s\e[0m \n" $tot_img<div></div>for img in $(cat links); do<div></div>let count_img++<br/>printf "\e[1;77m[\e[0m\e[1;31m+\e[0m\e[1;77m] Downloading image\e[0m\e[1;93m %s/%s\e[0m " $count_img $tot_img<br/>wget $img -O $user/images/image$count_img.jpg > /dev/null 2>&1<br/>printf "\e[1;92mDONE!\n\e[0m"<br/>done<br/>printf "\e[1;77m[\e[0m\e[1;31m+\e[0m\e[1;77m] Saved:\e[0m\e[1;93m %s/images/\e[0m\n" $user<div></div>cat $user_account.saved_ig.* > $user_account.raw_saved<br/>grep -o 'https://[^ ]*.mp4[^\ ]*.' $user_account.raw_saved | cut -d '"' -f1 | tr -d '\\' | uniq > vid_$user  <br/>count=0<br/>tot_vid=$(wc -l vid_$user | cut -d " " -f1)<br/>if [[ ! -d $user/videos ]]; then<br/>mkdir -p $user/videos<br/>fi<div></div>printf "\e[1;77m[\e[0m\e[1;31m+\e[0m\e[1;77m] Total Videos:\e[0m\e[1;93m %s\e[0m\n" $tot_vid<br/>for link in $(cat vid_$user); do<br/>let count++<br/>printf "\e[1;77m[\e[0m\e[1;31m+\e[0m\e[1;77m] Downloading video\e[0m\e[1;93m %s/%s\e[0m " $count $tot_vid<br/>printf "\e[1;92mDONE!\n\e[0m"<br/>wget $link -O $user/videos/video$count.mp4 > /dev/null 2>&1<br/>done<div></div>printf "\e[1;77m[\e[0m\e[1;31m+\e[0m\e[1;77m] Saved:\e[0m\e[1;93m %s/videos/\e[0m\n" $user <div></div><br/>}<div></div><br/>get_following() {<div></div>user_id=$(curl -L -s 'https://www.instagram.com/'$user_account'' > getid && grep -o  'profilePage_[0-9]*.' getid | cut -d "_" -f2 | tr -d '"')<div></div>curl -L -b cookie.$user -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"' -w "\n%{http_code}\n" -H "$header" "https://i.instagram.com/api/v1/friendships/$user_id/following" > $user_account.following.temp<div></div><br/>cp $user_account.following.temp $user_account.following.00<br/>count=0<div></div>while [[ true ]]; do<br/>big_list=$(grep -o '"big_list": true' $user_account.following.temp)<br/>maxid=$(grep -o '"next_max_id": "[^ ]*.' $user_account.following.temp | cut -d " " -f2 | tr -d '"' | tr -d ',')<div></div>if [[ $big_list == *'big_list": true'* ]]; then<div></div>url="https://i.instagram.com/api/v1/friendships/6971563529/following/?rank_token=$user_id\_$guid&max_id=$maxid"<div></div>curl -L -b cookie.$user -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"'  -H "$header" "$url" > $user_account.followers.temp<div></div>cp $user_account.following.temp $user_account.following.$count<div></div>unset maxid<br/>unset url<br/>unset big_list<br/>else<br/>grep -o 'username": "[^ ]*.' $user_account.following.* | cut -d " " -f2 | tr -d '"' | tr -d ',' | sort > $user_account.following_temp<br/>cat $user_account.following_temp | uniq > $user_account.following_backup<br/>rm -rf $user_account.following_temp<div></div>tot_following=$(wc -l $user_account.following_backup | cut -d " " -f1)<br/>printf "\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Total Following:\e[0m\e[1;77m %s\e[0m\n" $tot_following<br/>printf "\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Saved:\e[0m\e[1;77m %s.following_backup\e[0m\n" $user_account<div></div><br/>if [[ ! -d $user_account/raw_following/ ]]; then<br/>mkdir -p $user_account/raw_following/<br/>fi<br/>cat $user_account.following.* > $user_account/raw_following/backup.following.txt<br/>rm -rf $user_account.following.*<br/>break<div></div>fi<br/>echo $count<br/>let count+=1<div></div>done<div></div><div></div>}<div></div>total_followers() {<div></div>printf "\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Creating followers list for user\e[0m \e[1;77m%s\e[0m\n" $user_account<br/>printf "\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Please wait...\e[0m\n"<div></div><br/>user_id=$(curl -L -s 'https://www.instagram.com/'$user_account'' > getid && grep -o  'profilePage_[0-9]*.' getid | cut -d "_" -f2 | tr -d '"')<div></div>curl -L -b cookie.$user -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"' -w "\n%{http_code}\n" -H "$header" "https://i.instagram.com/api/v1/friendships/$user_id/followers/" > $user_account.followers.temp<div></div>cp $user_account.followers.temp $user_account.followers.00<br/>count=0<div></div><br/>while [[ true ]]; do<br/>big_list=$(grep -o '"big_list": true' $user_account.followers.temp)<br/>maxid=$(grep -o '"next_max_id": "[^ ]*.' $user_account.followers.temp | cut -d " " -f2 | tr -d '"' | tr -d ',')<div></div>if [[ $big_list == *'big_list": true'* ]]; then<div></div>url="https://i.instagram.com/api/v1/friendships/$user_id/followers/?rank_token=$user_id\_$guid&max_id=$maxid"<div></div>curl -L -b cookie.$user -s --user-agent 'User-Agent: "Instagram 10.26.0 Android (18/4.3; 320dpi; 720x1280; Xiaomi; HM 1SW; armani; qcom; en_US)"'  -H "$header" "$url" > $user_account.followers.temp<div></div>cp $user_account.followers.temp $user_account.followers.$count<div></div>unset maxid<br/>unset url<br/>unset big_list<br/>else<br/>grep -o 'username": "[^ ]*.' $user_account.followers.* | cut -d " " -f2 | tr -d '"' | tr -d ',' > $user_account.followers_backup<div></div>tot_follow=$(wc -l $user_account.followers_backup | cut -d " " -f1)<br/>printf "\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Total Followers:\e[0m\e[1;77m %s\e[0m\n" $tot_follow<br/>printf "\e[1;31m[\e[0m\e[1;77m+\e[0m\e[1;31m]\e[0m\e[1;93m Saved:\e[0m\e[1;77m %s.followers_backup\e[0m\n" $user_account<br/>if [[ $user == $user_account ]]; then<div></div>if [[ ! -d $user/raw_followers/ ]]; then<br/>mkdir -p $user/raw_followers/<br/>fi<div></div>cat $user.followers.* > $user/raw_followers/backup.followers.txt<br/>rm -rf $user.followers.*<div></div>break<div></div><br/>else<br/>if [[ ! -d $user_account/raw_followers/ ]]; then<br/>mkdir -p $user_account/raw_followers/<